import React, { useReducer, userState } from 'react'

const initialState = { count: 0 }
const reducer = (state, action) => {
    if (action.type === 'incr') {
        return { count: state.count + 1 }
    }
    if (action.type === 'decr') {
        return { count: state.count - 1 }
    }
