export default function UseReducer() {
    const [state, dispatch] = useReducer(reducer, initialState)
    return (
        <>
            <h1>{state.count}</h1>
            <div>
                <button onClick={() => dispatch({ 'type': 'incr' })}>Increament</button>
                <button onClick={() => dispatch({ 'type': 'decr' })}>Decreament</button>
            </div>
        </>
    )
}
